mapper.select:
select * from notice where nno in(
(select nno from notice where nno < #{nno} order by nno desc limit 1), (select nno from notice where nno > #{nno} order by nno limit 1)
)

select * from deal where dno in(
(select dno from deal where dno < #{dno} order by dno desc limit 1), (select dno from deal where dno > #{dno} order by dno limit 1)
)
