mapper.select:
select *
from(
select @rownum:=@rownum+1 rownum, n.*
from notice
n,(select @rownum:=0) as tmp

	where title like concat('%',#{keyword},'%')

order by nno desc
) as noticelist

	where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}

mapper.update:
update notice
set cnt = cnt + 1
where nno = #{nno}
